mapper.update:
UPDATE TR_ERS_INCIDENT SET TYPE=#{type,jdbcType=VARCHAR}, SUBTYPE=#{subtype,jdbcType=INTEGER}, INCIDENTLEVEL=#{incidentLevel,jdbcType=INTEGER}, SOURCE=#{source,jdbcType=VARCHAR}, STARTTIME=#{startTime,jdbcType=DATE}, SUBMITTIME=#{submitTime,jdbcType=DATE}, ENDTIME=#{endTime,jdbcType=DATE}, ROAD=#{road,jdbcType=VARCHAR}, ADDRESS=#{address,jdbcType=VARCHAR}, POINT=#{point,jdbcType=VARCHAR}, X=#{x,jdbcType=DOUBLE}, Y=#{y,jdbcType=DOUBLE}, ORIENTATION=#{orientation,jdbcType=VARCHAR}, EFFECTEDLANE=#{effectedLane,jdbcType=VARCHAR}, INCIDENTDESC=#{incidentDesc,jdbcType=VARCHAR}, TRAFFICJAM=#{trafficJam,jdbcType=VARCHAR}, INJURIED=#{injuried,jdbcType=VARCHAR}, OTHER=#{other,jdbcType=VARCHAR}, STATE=#{state,jdbcType=VARCHAR}, INCIDENT_NAME=#{incidentName,jdbcType=VARCHAR}, EXECUTE_STATUS=#{executeStatus,jdbcType=INTEGER}, ROAD_TYPE=#{roadType,jdbcType=VARCHAR}, VEHICLE_NUM=#{vehicleNum,jdbcType=INTEGER}, INJURED_NUM=#{injuredNum,jdbcType=INTEGER}, DEATH_NUM=#{deathNum,jdbcType=INTEGER}, IS_CONGESTION=#{isCongestion,jdbcType=VARCHAR}, CONGESTION_START=#{congestionStart,jdbcType=DATE}, CONGESTION_END=#{congestionEnd,jdbcType=DATE},
IS_SIMULATE=#{isSimulate,jdbcType=VARCHAR},
GEOGRAPHIC_INFORMATION=#{geographicInformation,jdbcType=VARCHAR}, ADMINISTRATIVE_OFFICE=#{administrativeOffice,jdbcType=VARCHAR}, SECTION=#{section,jdbcType=VARCHAR}, INTERVAL_START=#{intervalStart,jdbcType=VARCHAR}, INTERVAL_END=#{intervalEnd,jdbcType=VARCHAR}, SPOT_FINAL_REPORT=#{spotFinalReport,jdbcType=VARCHAR}, CENTRE_FINAL_REPORT=#{centreFinalReport,jdbcType=VARCHAR}, INCIDENT_NO=#{incidentNo,jdbcType=VARCHAR}, CAR_MODULE=#{carModuleStr,jdbcType=VARCHAR}, ACCIDENT_REASON=#{accidentReason,jdbcType=VARCHAR}, ACCIDENT_FORM=#{accidentForm,jdbcType=VARCHAR}, TRAFFIC_STOP=#{trafficStop,jdbcType=INTEGER} WHERE ID = #{id,jdbcType=VARCHAR}

mapper.select:
select * from TR_ERS_INCIDENT


		and TYPE = #{type, jdbcType=VARCHAR}


		and SECTION = #{section, jdbcType=VARCHAR}


		and ADMINISTRATIVE_OFFICE = #{administrativeOffice, jdbcType=VARCHAR}


		and STARTTIME BETWEEN #{startTime,jdbcType=DATE} AND #{endTime,jdbcType=DATE}

	ORDER BY STARTTIME DESC
